mapper.update:
update users
set account_number=#{accountNumber}, eco_challenge_id=#{ecoChallengeId}
where user_id = #{userId}

update users
set account_number=null, eco_challenge_id=null
where user_id = #{userId}

update account
set balance=balance+#{amount}
where account_id = (select account_id from users where user_id =#{id})
